import Layout from "../components/Layout";
import fetch from "isomorphic-unfetch";
import { useEffect, useReducer } from "react";
import InputSearch from '../components/InputSearch';
import foodSearchReducer from '../components/FoodSearchReducer';
import myKeys from '../myKeys.json';
import Display from '../components/Display';

let initialState = {};
let previousState = {};

// if(typeof window !== 'undefined' && window.document){ //check client-side
//   if(localStorage.getItem("FoodData")){
//     previousState = JSON.parse(localStorage.getItem('FoodData')); //working here
//   }
// }

// if(previousState){
//   initialState = previousState; 
// }
// else{
//   initialState = {
//     data: "",
//     inputQuery: "",
//     isSearch: false,
isLocalStorage: false
//   }
// }
initialState = {
      data: "",
      inputQuery: "",
      isSearch: false,
      isLocalStorage: false
    }
//working here. Trying to work on back button

const FoodSearch = (props) => {

  const [state, dispatch] = useReducer(foodSearchReducer, initialState);

  useEffect(() => {
    const fetchData = async () => {
      console.log("prev state input: ", previousState.inputQuery);
      const currentInputQuery = state.inputQuery
      const searchURL = `https://api.nal.usda.gov/fdc/v1/foods/search?api_key=${myKeys.usda}&query=${currentInputQuery}`;
      const resp = await fetch(searchURL);
      const jsonData = await resp.json();
      dispatch({
        type: "FETCH-DATA",
        value: jsonData,
        payload: true
      });
      dispatch({
        type: "IS-SEARCH",
        value: false
      });

    };
    
    if (state.isSearch) {
      fetchData();
    }

  }, [state.isSearch]);

  useEffect(() =>{
    if(typeof window !== 'undefined' && window.document){ //check client-side
      localStorage.setItem("FoodData", JSON.stringify(state));
      //initialState = JSON.parse(localStorage.getItem('FoodData'));
    }
    console.log("I am called");
  }, [state.isLocalStorage])

  const handleInput = (event) => {
    dispatch({
      type: "INPUT-QUERY",
      value: event.target.value
    })
  };

  const handleSearchButton = () => {
    dispatch({
      type: "SEARCH"
    })
  };

  // if(state.data){    
  //   if(typeof window !== 'undefined' && window.document){ //check client-side
  //     localStorage.setItem("FoodData", JSON.stringify(state));
  //   }
  // }

  return (
    <Layout>
      <InputSearch
        inputQuery={state.inputQuery}
        handleInput={handleInput}
        handleSearchButton={handleSearchButton}
      />
      <Display data={state.data}/>

    </Layout>
  );
};

export default FoodSearch;
