import { useRouter } from 'next/router';
import Link from 'next/link';

const FoodDetailsPage = () => {
  let brandOwner, ingredients, nutrients, unitName;
  const router = useRouter();
  const fdcId = router.query.id;
  if(typeof window !== 'undefined' && window.document){ //check client-side
    const FoodData = JSON.parse(localStorage.getItem('FoodData'));
    //console.log("data in detailed page", FoodData.data.foods);
    if(FoodData.data.foods){
      for(let [key, value] of Object.entries(FoodData.data.foods)){
        if(Number(fdcId) === Number(value.fdcId)){
          brandOwner = value.brandOwner;
          ingredients = value.ingredients;
          nutrients = value.foodNutrients;
          break;
        }
      }
    }

  }

  return (
    <div>
    <Link href="/">
      <a>Back</a>
    </Link>
      <h3>Brand: {brandOwner}</h3>
      <h3>ingredients: {ingredients}</h3>
      <h3>Nutrients:</h3>
      { 
      <ul>
        { nutrients &&
          nutrients.map((nutrient) => (
            <li key={nutrient.nutrientId}>{nutrient.nutrientName} -{nutrient.value} {nutrient.unitName}</li>
          )) }
      </ul>
      }
    </div>
  );
};
  

export default FoodDetailsPage;
