import { useRouter } from 'next/router';
import Link from 'next/link';
import { useEffect, useState } from 'react';
import fetch from "isomorphic-unfetch";
import myKeys from '../../myKeys.json';

const FoodDetailsPage = () => {
  const router = useRouter();
  const fdcId = router.query.id;
  const [foodDetails, setFoodDetails] = useState(null);

  useEffect(() =>{
    const fetchData = async () =>{
      const response = await fetch(`https://api.nal.usda.gov/fdc/v1/food/${fdcId}?api_key=${myKeys.usda}`);
      const jsonData = await response.json();
      setFoodDetails(jsonData);
    }

    fetchData();
  }, [fdcId])
  
  if(foodDetails){
    console.log("fooddetails: ", foodDetails);
    const { brandOwner, ingredients } = foodDetails;
  }


  return (
    <div>
    <Link href="/">
      <a>Back</a>
    </Link>
    {
      brandOwner.length && brandOwner ? <div>
      <h3>Brand: {brandOwner}</h3>
      <h3>ingredients: {ingredients}</h3>
      </div> : <h3>No data found</h3>
    }

      
    </div>
  );
};
  

export default FoodDetailsPage;
