import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';
import { useState } from 'react';
//import Axios from 'axios';

const Index = (props) => {
    const {totalHits, currentPage, totalPages, foods} = props.data;
    

    
    return ( <Layout>
        <h1>Hello 21Menus</h1>
        <p>Total Hits: {totalHits}</p>
        <p>currentPage: {currentPage}</p>
        <p>totalPages: {totalPages}</p>
        <input type="text" onChange={handleInputChange} placeholder="Cheddar Cheese" />
        <ul>
        {
            foods.map(food =><li key={food.fdcId}>{food.fdcId} - {food.brandOwner}</li>)
        }
        </ul>

        </Layout>);
}

Index.getInitialProps = async () => {
    const URL = `https://api.nal.usda.gov/fdc/v1/foods/search?api_key=GiSVQi6vc4bkITDFpqevACfryGTKgtKeRaE6FR98&query=Cheddar%20Cheese`;
    const response = await fetch(URL);
    const data = await response.json();
    return {
        data: data
    }

}
export default Index;
