import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';
import { useState, useEffect } from 'react';
//import Axios from 'axios';

const Index = (props) => {

    const {totalHits, currentPage, totalPages, foods} = props.data || "";
    const [inputQuery, setInputQuery] = useState("Cheddar Cheese");
    console.log("input Query: ", inputQuery);
    const [isSearch, setSearch] = useState(false);

    useEffect(() =>{
        const fetchData = async () =>{
            console.log("I am in fetchData");
            const URL = `https://api.nal.usda.gov/fdc/v1/foods/search?api_key=GiSVQi6vc4bkITDFpqevACfryGTKgtKeRaE6FR98&query=${inputQuery}`;
            const resp = await fetch(URL);
            const data = await resp.json();
            const {totalHits, currentPage, totalPages, foods} = await data;
            console.log("data from useEffect: ", data);
            setSearch(false);
        }
        fetchData();
    }, [isSearch]);

    // useEffect(() =>{
    //     setSearch(true);
    // }, [setInputQuery]);

    // useEffect(() => {
    //     setSearch(false);
    // }, []);

    return ( <Layout>
        <h1>Hello 21Menus</h1>
        <p>Total Hits: {totalHits}</p>
        <p>currentPage: {currentPage}</p>
        <p>totalPages: {totalPages}</p>
        <input type="text" value = {inputQuery}  placeholder="Cheddar Cheese" />
        <button onClick={() => setInputQuery(inputQuery)}>Search</button>
        <ul>
        {
            foods && foods.map(food =><li key={food.fdcId}>{food.fdcId} - {food.brandOwner}</li>)
        }
        </ul>

        </Layout>);
}

// Index.getInitialProps = async () => {
//     const URL = `https://api.nal.usda.gov/fdc/v1/foods/search?api_key=GiSVQi6vc4bkITDFpqevACfryGTKgtKeRaE6FR98&query=Cheddar%20Cheese`;
//     const response = await fetch(URL);
//     const data = await response.json();
//     return {
//         data: data
//     }

// }
export default Index;
