import Layout from '../../components/Layout'
import { useRouter } from 'next/router';
import { useContext } from 'react';
import { FoodContext } from '../../components/FoodContext';

const FoodDetailsPage = () => {
  const router = useRouter();
  const fdcId = router.query.id;
  const data = useContext(FoodContext);
  const foods = props.data.filter(food => food.fdcId===fdcId);
  return (
    <div>
      <h3>{fdcId}</h3>
      
      <p>{ foods && data.foods.ingredients }</p>
      <ul>
        { data.foods &&
          data.foods.foodNutrients.map((nutrient) => (
            <li key={nutrient.nutrientId}>{nutrient.nutrientId}</li>
          )) }
      </ul>
    </div>
  );
};
  

export default FoodDetailsPage;
