import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';
import { useState, useEffect, useRef } from 'react';

const Index = (props) => {
    const [data, setFetchData] = useState("");
    //const {totalHits, currentPage, totalPages, foods} = props.data || "";
    const [inputQuery, setInputQuery] = useState("");
    const [isSearch, setSearch] = useState(false);
    const inputRef = useRef(null);
    useEffect(() =>{
        const fetchData = async () =>{
            const searchURL = `https://api.nal.usda.gov/fdc/v1/foods/search?api_key=GiSVQi6vc4bkITDFpqevACfryGTKgtKeRaE6FR98&query=${inputQuery}`;
            const resp = await fetch(searchURL);
            const jsonData = await resp.json();
            setFetchData(jsonData);
            setSearch(false);
        }

        if(isSearch){
            fetchData();
        }
        
    }, [isSearch]);

    const handleSearchClick = () => {
        setSearch(true);
    }
    return ( <Layout>
        <h1>Hello 21Menus</h1>

        <input type="text" value={inputQuery} onChange={(event) => setInputQuery(event.target.value)}/>
        <button onClick={() => setSearch(true)}>Search</button>
        <ul>
        {
            data.foods && data.foods.map(food =><li key={food.fdcId}>{food.fdcId} - {food.brandOwner}</li>)
        }
        </ul>

        </Layout>);
}

